public class StringManipulator{public string ConcatenateStrings(string str1, string str2){return str1 + str2;}public bool IsStringNullOrEmpty(string str){return string.IsNullOrEmpty(str);}public bool StartsWithUppercaseLetter(string str){if (string.IsNullOrEmpty(str)){return false;}char firstChar = str[0];return char.IsUpper(firstChar);}public void ThrowException(){throw new InvalidOperationException("Invalid operation");}public List<string> GetStringList(){return new List<string> { "apple", "banana", "cherry", "date", "elderberry" };}}